//  Free Shipping
.halo-free-shipping-message {
  margin-bottom: 20px;
  .progress-shipping {
    width: 100%;
    height: 14px;
    margin: 0 0 10px 0;
    background-color: #ededed;
    .progress-meter {
      position: relative;
      display: block;
      height: 100%;
      text-align: center;
      font-size: 12;
      font-weight: 400;
      line-height: 1;
      color: #ffffff;
      -webkit-animation: 2s linear 0s infinite identifier;
      animation: 2s linear 0s infinite identifier;
      background-image: -webkit-linear-gradient(
        45deg,
        rgba(255, 255, 255, 0.15) 25%,
        transparent 25%,
        transparent 50%,
        rgba(255, 255, 255, 0.15) 50%,
        rgba(255, 255, 255, 0.15) 75%,
        transparent 75%,
        transparent
      );
      background-image: linear-gradient(
        45deg,
        rgba(255, 255, 255, 0.15) 25%,
        rgba(0, 0, 0, 0) 25%,
        rgba(0, 0, 0, 0) 50%,
        rgba(255, 255, 255, 0.15) 50%,
        rgba(255, 255, 255, 0.15) 75%,
        rgba(0, 0, 0, 0) 75%,
        rgba(0, 0, 0, 0)
      );
      background-size: 40px 40px;
      transition: 0.9s linear;
      transition-property: width, background-color;
      .progress-truck-wrapper {
        position: absolute;
        right: 0;
        bottom: -2px;
        .icon-shipping-truck {
          --truck-color: #ededed;
          width: 30px;
          fill:#6addda;
          height: 30px;
          stroke: #fff;
          transition: fill 350ms ease-in-out;
        }
      }
    }
    @keyframes identifier {
      0% {
        background-position: 0 0;
    }
    
    100% {
        background-position: 40px 0;
    }
    }
    &.progress-shipping-49 {
      .progress-meter {
        background-color: #e95144;
      }
    }
    &.progress-shipping-74 {
      .progress-meter {
          background-color: #ffc206;
        }
    }
    &.progress-shipping-99 {
      .progress-meter {
        background-color: #ffcb3d;
      }
    }

    &.progress-shipping-full {
      .progress-meter {
        background-color: #69c69c;
      }
    }
  }

  .shipping-message {
    font-size: 12;
    font-weight: 400;
    font-style: italic;
    color: #232323;
    letter-spacing: 0;
    line-height: 20px;
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;

    span {
      + span {
        font-style: normal;
      }
    }
  }

  .shipping-message-100 {
    display: block;
    text-align: center;
    padding: 0;
    margin: 0;
    color: #232323;
    font-size: 12;
    font-weight: 400;
    line-height: 1.75;
    letter-spacing: 0;
    background: transparent;
  }
}

.page-cart.has-calculate-shipping {
  [data-cart-status],
  .alertBox {
    display: none;
  }

  .halo-free-shipping-message {
    padding-top: 0;
    padding-bottom: 30px;

    .progress-shipping {
      height: 12px;
    }

    .shipping-message-100 {
      padding-top: 11px;
      padding-bottom: 11px;
      background: #fafafa;
    }
  }
}

.previewCart.previewCart2 {
  .halo-free-shipping-message {
    padding: 0 0 35px 0;
    .shipping-message-100 {
      background: #f7f7f7;
      padding: 10px 0;
      span {
        font-size: 15px;
        color: #5fb139;
      }
    }
  }
}

.previewCart {
  .halo-free-shipping-message {
    .shipping-message-100 {
      background: #f7f7f7;
      padding: 4px 0;
      span {
        font-size: 14px;
        color: #5fb139;
      }
    }

    @media (min-width: 1025px) {
    }
  }
}

.page-cart {
  &.has-calculate-shipping {
    .halo-free-shipping-message {
      .shipping-message-100 {
        background: #f7f7f7;
        span {
          color: #5fb139;
          font-size: 15px;
        }
      }
    }
  }
}

// Sticky Toolbar Mobile
