// Recently Bought Popup
// -----------------------------------------------------------------------------
#recently_bought_list {
  left: 20px;
  right: auto;
  bottom: 90px;
  position: fixed;
  z-index: 999;
  background: #fff;
  width: calc(100% - 30px);
  max-width: 368px;
  animation-duration: 0.5s;
  transition: all 0.5s ease;
  padding: 0;
  border-radius: 5px;
  -webkit-box-shadow: 0px 1px 10px 1px rgba(0, 0, 0, 0.1);
  box-shadow: 0px 1px 10px 1px rgba(0, 0, 0, 0.1);


  @media (max-width: 480px) {
      left: 10px;
      max-width: 100%;
      width: calc(100% - 25px);
  }

  .halo-recently-bought {
      [data-close-recently-bought] {
          position: absolute;
          width: 15px;
          height: 15px;
          top: 7px;
          right: 15px;
          left: auto;
          text-align: center;
          text-decoration: none;
          z-index: 1;

          svg {
              width: 15px;
              height: 16px;
              fill: #969696;
              stroke: black;
          }

          @media(max-width: 550px) {
              top: 2px;
              right: 8px;
          }
      }
  }

  .recently-bought-inner {
      display: -webkit-box;
      display: -webkit-flex;
      display: -moz-flex;
      display: -ms-flexbox;
      display: flex;
      -webkit-align-items: flex-start;
      -moz-align-items: flex-start;
      -ms-align-items: flex-start;
      align-items: flex-start;
      -webkit-box-pack: flex-start;
      -moz-box-pack: flex-start;
      -ms-flex-pack: flex-start;
      -webkit-justify-content: flex-start;
      justify-content: flex-start;

      .product-image {
          max-width: 70px;
          display: inline-block;
          position: relative;
          padding: 0;
          margin-top: 10px;
          margin-left: 5px;

          @media (max-width: 550px) {
              margin-top: 5px;
          }
      }

      .product-info {
          width: calc(100% - 80px);
          padding: 8px 25px 7px 10px;
          line-height: 20px;

          @media (min-width: 551px) {
              padding: 15px 35px 15px 15px;
          }
          .text-wrap {
              display: inline-block;
              width: 100%;
              color: #969696;
              font-weight: 500;
              margin-bottom: 5px;
              line-height: 20px;
              letter-spacing: 0;

              @media (min-width: 551px) {
                  margin-bottom: 5px;
              }
          }

          .product-name {
              overflow: hidden;
              text-overflow: ellipsis;
              -webkit-line-clamp: 2;
              display: -webkit-box;
              -webkit-box-orient: vertical;
              /* autoprefixer: off */

              a {
                  text-decoration: none;
                  font-weight: bold;
                  color: #323232;
              }
          }

          .location-info {
              font-size: 11px;
              color: #969696;
              line-height: 20px;
              letter-spacing: 0.02em;
              margin-top: 0px;
              font-style: italic;

              @media (max-width: 767px) {
                  overflow: hidden;
                  text-overflow: ellipsis;
                  -webkit-line-clamp: 1;
                  display: -webkit-box;
                  -webkit-box-orient: vertical;
              }
          }
      }
  }
}
