// Add to cart
#sticky_addtocart{
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  z-index: 101;
  padding: 15px 0;
  text-align: center;
  background: #ffffff;
  transition: all 0.5s ease;
  transform: translate3d(0, 100%, 0);
  box-shadow: 0px 0px 4px rgba(0,0,0,0.2);
  @media (max-width:1024px) {
    bottom:68px;
  }
  > .container {
      display: flex;
      position: static;
      align-items: center;
      justify-content: center;
      justify-content: space-between;
      &:before, &:after {
          content: none;
      }
      .item {
          width: 100%;
          display: inline-block;
          vertical-align: middle;
          &:first-child {
              display: none;
          }
      }
  }
  .countDown {
      display: none;
  }
  .product-wrapper {
      display: table;
      max-width: 360px;
      text-align: left;
      > .product-image {
          display: table-cell;
          vertical-align: middle;
          width: 70px;
          > img {
              max-width: 70px;
          }
      }
      > .product-content {
          display: table-cell;
          vertical-align: middle;
          padding-left: 10px;
      }
      .productView-title {
          font-size: 12px;
          line-height: 20px;
          font-weight: 700;
          margin: 0 0 8px 0;
          font-family: 'Museo-300';
          color: stencilColor('card-title-color');
      }
      .productView-price {
          margin: 0;
          font-size: 0;
          position: relative;
          .price-section {
              font-size: 14px;
              line-height: 1;
              font-weight: 700;
              color: stencilColor('card-price-color');
              span[data-product-rrp-without-tax] {
                  
              }
              &.price-section--withoutTax {
                  color: stencilColor('card-sale-price-color');
                  .price-now-label + .price--withoutTax {
                      color: stencilColor('card-price-color');
                  }
              }
              &.rrp-price--withoutTax {
                  color: stencilColor('card-resale-price-color');
                  font-size: 14px;
                  font-weight: 500;
                  margin-bottom: 5px;
              }
              &.non-sale-price--withoutTax {
                  color: stencilColor('card-resale-price-color');
                  margin-right: 8px;
                  display: inline-block;
                  vertical-align: top;
                  + .price-section--withoutTax {
                      display: inline-block;
                      vertical-align: top;
                  }
              }
              &.price-none {
                  margin-right: 0;
                  + .price-section--withoutTax {
                      color: stencilColor('card-price-color');
                  }
              }
          }
          .price-section--saving {
              color: stencilColor('card-price-color');
              margin-top: 8px;
              font-size: 12px;
              font-weight: 400;
              font-style: italic;
          }
          .sold-product {
              display: block;
              top: -1px;
              right: 0;
              margin-top: 0;
              position: absolute;
              @media (min-width: 1025px) {
                  top: -1px;
                  right: 0;
                  margin-top: 0;
                  position: absolute;
              }
          }
      }
      
  }
  .form-field--increments {
      margin: 0 10px;
      font-size: 0;
      display: none;
      .form-increment {
          .button {
              height: 39px;
              width: 39px;
              margin: 0;
              border-radius: 0;
              > .icon {
                  line-height: 39px;
                  width: 23px;
                  height: 23px;
              }
              img{ 
                  height: 8px;
                  width: 12px;
              }
          }
          .form-input--incrementTotal {
              height: 39px;
              width: 60px;
              line-height: 39px;
              border: 1px solid #88dad9;
              border-radius: 0;
              border-left: 0;
              border-right: 0;
              
          }
      }
      .form-label {
          display: none;
      }
  }
  .form-action {
      position: relative;
      width: 100%;
      display: inline-block;
      input {
          margin-bottom: 0;
          width: 100%;
      }
      .choose_options_add {
          width: 100%;
          margin: 0;
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          opacity: 1;
          visibility: visible;
          z-index: 5;
          &.is-active {
              opacity: 0;
              visibility: hidden;
          }
      }
  }
  .show_select_options{
      color: black;
      border: 1px solid #cbcbcb;
      background-color: rgba(0, 0, 255, 0);
      min-width: 209px;
      font-size: 12px;
      line-height: 24px;
      font-size: 12px;
      line-height: 24px;
      border-radius: 3px;
      letter-spacing: 0.65px;
      text-transform: uppercase;
      transition: all 0.3s ease;
      font-family: "Poppins", Arial, Helvetica, sans-serif;
  }
  .form {
      margin: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      .pop-up-option {
          left: 0;
          right: 0;
          bottom: -100%;
          width: 100%;
          z-index: -1;
          position: absolute;
          text-align: left;
          background: #ffffff;
          padding: 25px 0 10px 0;
          opacity: 0;
          overflow: visible;
          visibility: hidden;
          transition: all .3s ease;
          box-shadow: 0 0 0 black, 0 0 1px rgba(0, 0, 0, 0.1);
          &.is-open {
              visibility: visible;
              opacity: 1;
              bottom: 100%;
          }
          .data-product-name {
              display: block;
              margin-bottom: 20px;
              padding-bottom: 10px;
              padding-right: 30px;
              border-bottom: 1px solid #f8f8f8;
              .productView-title {
                  font-size: 15px;
                  margin-bottom: 8px;
                  font-family: 'Museo-300';
                  color: stencilColor('card-title-color');
              }
              .productView-price .price-section {
                  font-size: 16px;
                  font-weight: 700;
              }
          }
          .data-product-option {
              font-size: 0;
              display: block;
              width: 100%;
          }
          .form-field {
              width: 100%;
              font-size: 12px;
              margin: 0 0 10px 0;
              display: block;
              vertical-align: top;
              position: relative;
              .form-label {
                  display: block;
                  margin-right: 7px;
                  margin-bottom: 7px;
                  > small {
                      float: none;
                  }
              }
              &[data-product-attribute] .form-option {
                  display: inline-block;
                  vertical-align: middle;
              }
          }
          .close {
              font-size: 20px;
              color: #000;
              position: absolute;
              right: 10px;
              width: 40px;
              height: 40px;
              line-height: 40px;
              top: -15px;
              opacity: 1;
              background: #fff;
              filter: alpha(opacity=100);
              text-align: center;
              font-weight: 600;
              z-index: 10;
              text-decoration: none;
          }
      }
      .alertBox--error {
          min-width: 200px;
          margin: 0;
          opacity: 0.65;
          width: 100%;
          color: #fff;
          border-radius: 0;
          text-align: center;
          padding: 10px 25px;
          pointer-events: none;
          background-color: #505050;
          border: 1px solid #505050;
          .alertBox-icon {
              display: none;
          }
          .alertBox-message {
              font-size: 13px;
              line-height: 20px;
              text-transform: uppercase;
              letter-spacing: 0.05rem;
              font-weight: 700;
              padding: 0;
              text-align: center;
              display: block;
          }
      }
  }
  .button.button--icon{
      svg{
          width: 100%;
          max-width: 16px;
      }
   }
  @media (min-width: 768px) {
      > .container {
          .item {
              width: auto;
              &:first-child {
                  display: inline-block;
              }
          }
      }
      .form {
          .pop-up-option {
              padding: 35px 0 20px 0;
              .form-field {
                  font-size: 12px;
                  margin: 0 0 10px 0;
                  display: inline-block;
                  vertical-align: top;
                  width: 50%;
                  &:nth-child(even) {
                      padding-left: 15px;
                  }
                  &:nth-child(odd) {
                      padding-right: 15px;
                  }
                  .form-label {
                      display: inline-block;
                      vertical-align: middle
                  }
              }
              .data-product-name {
                  display: none;
              }
              .data-product-option {
                  position: relative;
                  padding-right: 30px;
              }
              .close {
                  top: -5px;
                  right: -10px;
              }
          }
      }
      .form-field--increments {
          display: block;
      }
      .form-action {
          width: 200px;
      }
  }
  @media (min-width: 1025px) {
      .countDown {
          display: block;
          margin-right: 10px;
      }
  }
  .form-field--increments .form-increment .form-input--incrementTotal {
      background: unset !important;
      padding: unset !important;
      text-align: center;
      & ~ .button--icon svg {
          transform: rotate(180deg);
      }
  }
}
.show_sticky#sticky_addtocart {
  z-index: 105;
  -moz-transform: translate3d(0, 0, 0);
  -o-transform: translate3d(0, 0, 0);
  -ms-transform: translate3d(0, 0, 0);
  -webkit-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
  opacity: 1;
}

#halo-bundle-products .halo-product-list {
  display: flex;
  .halo-product-item {
    flex-basis: 25%;
  }
}
.halo-bundle-products{
  padding-top: 50px;
  padding-bottom: 50px;
  margin: 0 0 50px 0;
  input:focus, button:focus, textarea:focus, select:focus, details:focus, [href]:focus, [tabindex]:not([tabindex="-1"]):focus, [contenteditable="true"]:focus {
      outline: none !important;
      outline-offset: 0 !important;
  }
  .halo-block-header{
      padding-bottom:20px;
      text-align: center;
  }

  .bundle-products-wrapper{
      display: block;
  }

  .bundle-product-left{
      width: 100%;
  }

  .bundle-product-right{
      width: 100%;
      margin: 50px 0 0 0;
      @media (max-width: 1024px) {
        margin: 0;
        display: grid;
        place-content: center;
      }
      @media (max-width: 768px) {
        margin: 45px 0 0 0;
      }
  }

  .halo-product-list{
      position: relative;
      padding: 0;
      margin: 0 -15px;
      width: auto;
      &:not(.slick-initialized){
          display: block;
          white-space: nowrap;
          overflow: hidden;
          font-size: 0;
          letter-spacing: 0;
          position: relative;

          .halo-product-item{
              display: inline-block;
              vertical-align: top;
              width: auto;
              opacity: 0;
              visibility: hidden;
          }

          &:before{
              content: '';
              background: url(../img/loading.svg) no-repeat;
              background-size: contain;
              display: block;
              position: absolute;
              width: 200px;
              height: 200px;
              top: 50%;
              left: 50%;
              transform: translate(-50%, -50%);
          }
      }
      .slick-arrow{
        position: absolute;
