.scrollbar {
  overflow-y: auto;
  &::-webkit-scrollbar-track {
      background: #EAEAEA;
  }

  &::-webkit-scrollbar-thumb {
      background: #88dad9;
  }
}
.quickShopPopup {
  box-shadow: 12px 10px 15px -3px rgba(0,0,0,0.1);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  max-height: 100%;
  background-color: #ffffff;
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
  -webkit-transition: all 0.3s ease;
  -moz-transition: all 0.3s ease;
  -ms-transition: all 0.3s ease;
  -o-transition: all 0.3s ease;
  transition: all 0.3s ease;
  transform: translate3d(-50px, 0, 0);
  height: auto;
  z-index: 1;
  overflow-x: hidden;
  &.scrollbar::-webkit-scrollbar {
      width: 3px;
  }

  &.is-open {
      opacity: 1;
      visibility: visible;
      pointer-events: auto;
      transform: translate3d(0, 0, 0);
  }
  .halo-quickShop-header {
      padding: 20px;
      text-align: center;
      display: none;
      .halo-quickShop-title {
          color: stencilColor('color-textHeading');
          font-size: stencilNumber("fontSize-root");
          font-weight: fontWeight("bold");
      }
  }

  .halo-quickShop-content {
    padding: 20px 0 0 0;
    position: relative;
    .productView-details {
      float: none;
      width: 100%;
      .halo-productOptions {
          text-align: center;
          margin-bottom:0;
          [data-product-option-change] {
              .form-field {
                  margin-bottom: 10px;
                  padding: 0 15px;
                  .form-option-wrapper {
                    display: inline;
                    float: center;
                  }
              }
              .form-option-expanded {
                  display: none;
              }
              [data-product-attribute="set-rectangle"] {
                  .form-option {
                      border-color: transparent;
                      border-radius: 50%;
                      min-width: 40px;
                      height: 40px;
                      border-radius: 100px;
                  }

                  .form-radio:checked+.form-option {
                      font-weight: 700;
                      box-shadow: none;
                  }
              }
              [data-product-attribute="swatch"] {
                  .form-option-swatch {
                      width: 32px;
                      height: 32px;
                      border-color: transparent;
                      border-radius: 50%;
                      padding:3px;
                      &[for="attribute_swatch__none"] {
                          .form-option-variant--none {
                              min-width: 30px;
                              line-height: 24px;
                          }
                      }
                      span {
                        border-radius: 50%;
                      }
                  }

                  .form-radio:checked+.form-option {
                      border-color: #cfcfcf;
                      box-shadow: none;
                  }
              }
          }
          .form-increment {
              position: relative;
              max-width: 95px;
              width: 100%;
              display: block;
              margin: 0 auto;
              float: none;
              .button {
                margin: 0;
                padding: 0;
                border: none;
                width: 0.83333rem;
                height: 0.83333rem;
                position: absolute;
                right: 10px;
                background: 0 0;
                &[data-action=dec] {
                  bottom: 8px;
                  &:before {
                    border-top-color: #88dad9;
                    bottom: 2px;
                  }
              }
                &[data-action=inc] {
                  top: 8px;
                  &:before {
                    border-bottom-color: #88dad9;
                    top: 2px;
                  }
              }
              &::before {
                content: "";
                width: 1px;
                height: 1px;
                border: 4px solid transparent;
                position: absolute;
                left: 0;
              }
              }
              .form-input--incrementTotal {
                display: inline-block;
                color: #666666;
                font-size: 15px;
                padding: 10px 30px 10px 18px;
                vertical-align: middle;
                width: 100%;
                max-width: 95px;
                font-weight: 500;
                border-style: solid;
                border-width: 1px;
                border-radius: 4px;
                border-color:#88dad9;
              }
          }
          .alertBox {
              margin-bottom: 20px;
          }
          .form-action {
              width: 50%;
              padding-right: 10px;
              margin-right: 0;
              float:left;
              margin-top: unset;
              #form-action-addToCart {
                  height: auto;
                  margin-bottom: 0;
                  white-space: nowrap;
                  text-overflow: ellipsis;
                  overflow: hidden;
              }
          }

          .halo-quickShop-action {
              width: 50%;
              display: inline-block;
              padding-left: 10px;
              .halo-quickShop-close {
                  width: 100%;
                  margin-bottom: 0;
              }
          }
      }
      .form-label--alternate {
        font-weight: 700;
        float: none;
      }
    }
  }
  @media(max-width: 1199px) {
      max-height: 100%;
      .halo-quickShop-content .halo-productOptions {
          .form-increment {
              .form-input--incrementTotal {
                  height: 42px;
              }

              .button[data-action=dec] {
                  bottom: 8px;
              }

              .button[data-action=inc] {
                  top: 8px;
              }
          }

          .form-action {
              width: 100%;
              padding-right: 0;
              margin-top: unset;
              #form-action-addToCart {
                  height: unset;
                  padding: 12px 10px 13px;
                  font-size: stencilNumber("fontSize-root") - 3;
              }
          }

          .halo-quickShop-action {
              width: 100%;
              padding-left: 0;
              .halo-quickShop-close {
                  padding: 12px 10px 13px;
                  font-size: stencilNumber("fontSize-root") - 3;
              }
          }
      }
  }
}

