components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/halothemes/halo-navigation-li:
{{#if children}}
    {{#each children}}
        {{#if children}}
            <li class="navPages-item{{#if children}} has-dropdown{{/if}}">
                <p class="navPages-action navPages-action-lv1 navPages-action-depth-max has-subMenu is-root level-1" data-label="{{url}}" data-index="{{@index}}">
                    <a class="text" href="{{url}}" aria-label="{{name}}">
                        <span class="custom-image">
                             {{#if image}}
                                {{> components/common/responsive-img
                                    image=image
                                    fallback_size=theme_settings.zoom_size
                                    lazyload=theme_settings.lazyload_mode
                                    class="category-header-image"
                                }}
                            {{/if}}
                        </span>
                        <span>{{name}}</span>
                    </a>
                    <span class="navPages-action-moreIcon" aria-hidden="true">
                        <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
                    </span>
                    <span class="navPages-action-moreIcon-reverse" aria-hidden="true">
                        <svg class="icon"><use xlink:href="#icon-long-arrow-left"/></svg>
                    </span>
                </p>
                {{> components/halothemes/navigation-dropdown-custom settings=../../settings}}
            </li>
        {{else}}
            <li class="navPages-item">
                <a class="navPages-action navPages-action-lv1 no-subMenu navPages-action-end level-1" data-label="{{url}}" href="{{url}}" aria-label="{{name}}" data-index="{{@index}}">
                    <span class="custom-image">
                         {{#if image}}
                                {{> components/common/responsive-img
                                    image=image
                                    fallback_size=theme_settings.zoom_size
                                    lazyload=theme_settings.lazyload_mode
                                    class="category-header-image"
                                }}
                            {{/if}}
                    </span>
                  <span class="text">{{name}}</span>
                </a>
            </li>
        {{/if}}
    {{/each}}
{{/if}}

